const webhookData = $('Task Agent Webhook').first().json.body;
const tasks = $input.all().map(item => ({
  id: item.json.id,
  title: item.json.title,
  description: item.json.description,
  urgency: item.json.urgency,
  importance: item.json.importance,
  estimated_minutes: item.json.estimated_minutes,
  due_date: item.json.due_date,
  location: item.json.location,
  status: item.json.status,
  tags: item.json.tags,
  energy_level: item.json.energy_level,
  snoozed_until: item.json.snoozed_until
}));

return [{ json: { ...webhookData, active_tasks: tasks } }];

const rawMessage = $('Task Agent Webhook').first().json.body.message;
const userId = $('Task Agent Webhook').first().json.body.user_id;
const history = $('Task Agent Webhook').first().json.body.history || [];

let parsed;
try {
  let text = $input.first().json.output || $input.first().json.text || '';
  text = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  parsed = JSON.parse(text);
} catch (e) {
  const fallbackText = $input.first().json.output || $input.first().json.text
    || 'Could you tell me more about that?';
  return [{ json: {
    action: 'clarify',
    replies: [fallbackText],
    done: false
  }}];
}

const action = parsed.action;

if (action === 'create') {
  const tasks = (parsed.tasks || [parsed]).map(t => ({
    user_id: userId,
    raw_input: rawMessage,
    title: t.title || rawMessage,
    description: t.description || null,
    urgency: Math.max(1, Math.min(5, parseInt(t.urgency) || 3)),
    importance: Math.max(1, Math.min(5, parseInt(t.importance) || 3)),
    estimated_minutes: t.estimated_minutes ? parseInt(t.estimated_minutes) : null,
    due_date: t.due_date || null,
    location: t.location || null,
    tags: Array.isArray(t.tags) ? t.tags : [],
    energy_level: ['low','medium','high'].includes(t.energy_level) ? t.energy_level : 'medium',
    can_be_split: !!t.can_be_split,
    recurrence: t.recurrence || null,
    ai_conversation: history,
    status: 'pending'
  }));
  const replies = parsed.replies
    || (parsed.reply ? [parsed.reply] : tasks.map(t => 'Added: ' + t.title));
  return [{ json: { action: 'create', replies, done: true, tasks } }];
}

if (['complete', 'start', 'delete', 'snooze'].includes(action)) {
  return [{ json: {
    action,
    task_id: parsed.task_id || null,
    follow_up_minutes: parsed.follow_up_minutes || null,
    snoozed_until: parsed.snoozed_until || null,
    replies: parsed.replies || [parsed.reply],
    done: true
  }}];
}

if (action === 'suggest' || action === 'clarify') {
  return [{ json: {
    action,
    task_id: parsed.task_id || null,
    replies: parsed.replies || [parsed.reply],
    done: false
  }}];
}

// Unknown action fallback
return [{ json: {
  action: 'clarify',
  replies: [parsed.reply || "I'm not sure what you mean. Could you rephrase?"],
  done: false
}}];
